import React, { useState, usestate } from 'react';
import Header from './Header';
import Footer from './Footer';
import CreateNote from './CreateNote';
import Note from './Note';

const App = () => {
  const [addItem, setAddItem] = useState([]);

  const addNote = (note) => {
    setAddItem((prevData) => {
      return [...prevData, note];
    });

    console.log(note);
  };

  return (
  <>
   <Header />
   <Footer />
   <CreateNote 
     passNote={addNote}
   />
   <Note />
  </>
  );
};

export default App;
