import React, {useState} from 'react';


const App = () => {

  const [fullName, setFullName] = useState({
    fName: "",
    lName: "",
    email: "",
    phone: "",
  });

  const onSubmits = (event) =>{
    event.preventDefault();
    alert("form subnitted");
  };

  const inputEvent = (event) =>{
    console.log(event.target.value);
    console.log(event.target.name);

    // const value = event.target.value;
    // const name = event.target.name;

    const {name,value} = event.target;
    
    setFullName((preValue) => {
      console.log(preValue);

      return{
        ...preValue,
      [name] : value,
      }

      // if(name === 'fName'){
      //   return {
      //   fName: value,
      //   lName: preValue.lName,
      //   email: preValue.email,
      //   phone: preValue.phone,
      // };
      // }else  if(name === 'lName'){
      //   return {
      //   fName: preValue.fName,
      //   lName: value,
      //   email: preValue.email,
      //   phone: preValue.phone,
      // };

      // }else  if(name === 'email'){
      //   return {
      //   fName: preValue.fName,
      //   lName: preValue.lName,
      //   email: value,
      //   phone: preValue.phone,
      // };
      // }else  if(name === 'phone'){
      //   return {
      //   fName: preValue.fName,
      //   lName: preValue.lName,
      //   email: preValue.email,
      //   phone: value,
      // };
      // }
    });

  };


  return(
  <>
     <div className='main_div'>
     <div>
      <h1> Hello {fullName.fName} {fullName.lName} </h1>
      <p> {fullName.email} </p>
      <p> {fullName.phone} </p>
      <input type='text' placeholder='Enter Your Name' 
        name='fName'
        onChange={inputEvent}value={fullName.fName}/>
      <br />
      <input type='text' placeholder='Enter Your Last Name' 
        name='lName'
        onChange={inputEvent}value={fullName.lName}
      />

      <input type='email' placeholder='Enter Your Email ' 
        name='email'
        onChange={inputEvent}value={fullName.email}
        autoComplete='off'
      />

      <input type='number' placeholder='Enter Your Number ' 
        name='phone'
        onChange={inputEvent}value={fullName.phone}
      />

      <button onSubmit={onSubmits}> Submit Me ❤️️ </button>
      
     </div>
     </div>
     

  </>
  );
}

export default App;
